const keys = [40];
const elem = document.evaluate('//div[@id=\'blog-post-info\']/div[2]/div[5]/div[2]/ul/li[2]/div[2]/input', document, null, XPathResult.ANY_TYPE, null).iterateNext();
keys.forEach((key) => {
    let eventObj = document.createEvent("Events");
    eventObj.initEvent("keydown", true, true);
    eventObj.key = key;
    eventObj.keyCode = key;
    eventObj.which = key;
    eventObj.charCode = key;

    elem.dispatchEvent(eventObj);
    eventObj = document.createEvent("Events");
    eventObj.initEvent("keypress", true, true);
    eventObj.key = key;
    eventObj.keyCode = key;
    eventObj.which = key;
    eventObj.charCode = key;
    elem.dispatchEvent(eventObj);

    eventObj = document.createEvent("Events");
    eventObj.initEvent("keyup", true, true);
    eventObj.key = key;
    eventObj.keyCode = key;
    eventObj.which = key;
    eventObj.charCode = key;
    elem.dispatchEvent(eventObj);
});

